                      value={typeFilter}
                      onChange={(e) => setTypeFilter(e.target.value)}
                    >
                      <option value="all">Todos los tipos</option>
                      {Object.entries(APPLICATION_TYPE_LABELS).map(([value, label]) => (
                        <option key={value} value={value}>{label}</option>
