import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import MainLayout from '../components/layout/MainLayout';
import ClientForm from '../components/clients/ClientForm';
import { Client, ClientDocument, createClient, updateClient } from '../services/clientService';
import Alert from '../components/ui/Alert';
import { UserContext } from '../contexts/UserContext';

// Default company ID (Herramental)
const DEFAULT_COMPANY_ID = "70b2aa97-a5b6-4b5e-91db-be8acbd3701a";

const NewClient: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { user: userContext } = useContext(UserContext);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [sectionSubmitting, setSectionSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [draftClient, setDraftClient] = useState<Partial<Client> | null>(null);

  const handleSubmit = async (data: Partial<Client>, documents: ClientDocument[]) => {
    try {
      setIsSubmitting(true);
      setError(null);
      setSuccessMessage(null);

      console.log('Creating new client with data:', data);

      // Si el usuario es asesor o admin de empresa, asignar los IDs correspondientes
      const clientData: Partial<Client> = { ...data };

      // Handle advisor_id assignment
      if (user && user.role === 'ADVISOR') {
        clientData.advisor_id = user.id;
      }

      // Handle the company_id requirement - only set default if not selected in form
      if (!clientData.company_id) {
        console.log('No company selected in form, applying default/user company logic');

        if (user) {
          if (user.role === 'COMPANY_ADMIN') {
            clientData.company_id = user.entityId;
            console.log('Using company_id from COMPANY_ADMIN user:', clientData.company_id);
          } else {
            // For other roles without company selection, use default
            clientData.company_id = DEFAULT_COMPANY_ID;
            console.log('Using default company_id for user with role:', user.role);
          }
        } else {
          // No user information available, use default company_id
          clientData.company_id = DEFAULT_COMPANY_ID;
          console.log('No user context, using default company_id');
        }
      } else {
        console.log('Using company_id selected in form:', clientData.company_id);
      }

      // Crear el cliente
      const newClient = await createClient(clientData as Omit<Client, 'id' | 'created_at'>, documents, user?.id);
      console.log('Client created successfully:', newClient);

      // Clear local storage
      localStorage.removeItem('clientFormData');
      localStorage.removeItem('clientFormStep');

      // Redireccionar a la lista de clientes
      navigate('/clients');
    } catch (error: any) {
      console.error('Error al crear cliente:', error);
      setError(error.message || 'Error al crear el cliente. Por favor, intente de nuevo.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Handle partial save functionality
  const handleSavePartial = async (data: Partial<Client>, step: number) => {
    try {
      console.log(` Guardando datos parciales del nuevo cliente en paso ${step}`);
      console.log(' Datos a guardar:', data);

      // Update the local data with this partial section
      const updatedData = { ...draftClient, ...data };
      setDraftClient(updatedData);

      // For first-time saves, we need to actually create the client in the database
      // This is particularly important for step 1 (personal data)
      if (!draftClient?.id) {
        console.log(' No hay ID de cliente existente, creando nuevo registro en Supabase');

        // Prepare client data with user associations
        const clientData: Partial<Client> = { ...updatedData };

        // Handle the company_id requirement - only set default if not selected in form
        ensureRequiredFields(clientData);

        try {
          // Create minimal client record with available data
          const newClient = await createClient(clientData as Omit<Client, 'id' | 'created_at'>, [], user?.id);
          console.log(' Nuevo registro de cliente creado con ID:', newClient.id);

          // Update draftClient with the newly created client including ID
          const newDraftClient = {...updatedData, id: newClient.id, created_at: newClient.created_at};
          setDraftClient(newDraftClient);

          // Create a backup in case the user refreshes
          try {
            localStorage.setItem('clientFormData', JSON.stringify(newDraftClient));
            localStorage.setItem('clientFormStep', String(step));
          } catch (err) {
            console.error(' Error guardando estado del formulario:', err);
          }

          // Show success message for first creation
          setSuccessMessage(`Se cre贸 el cliente exitosamente y se guardaron los datos de la secci贸n ${step}`);
          setTimeout(() => setSuccessMessage(null), 3000);

          return newClient;
        } catch (err: any) {
          console.error(' Error creando registro inicial del cliente:', err);
          throw new Error(`No se pudo crear el registro del cliente: ${err.message}`);
        }
      } 
      else {
        console.log(` Cliente ya existe con ID ${draftClient.id}, actualizando datos parciales`);

        // Para las actualizaciones posteriores, necesitamos actualizar el registro existente
        try {
          // Preparar datos para la actualizaci贸n
          const updateData: Partial<Client> = collectFieldsForStep(data, step);
          updateData.id = draftClient.id; // Asegurar que el ID est茅 incluido

          console.log(' Datos preparados para actualizaci贸n:', updateData);

          // Actualizar el cliente
          const updatedClient = await updateClient(draftClient.id, updateData);
          console.log(' Cliente actualizado exitosamente:', updatedClient);

          // Actualizar el estado local con los datos actualizados
          const newDraftClient = {...draftClient, ...data};
          setDraftClient(newDraftClient);

          // Respaldar en localStorage
          try {
            localStorage.setItem('clientFormData', JSON.stringify(newDraftClient));
            localStorage.setItem('clientFormStep', String(step));
          } catch (err) {
            console.error(' Error guardando estado del formulario:', err);
          }

          // Mostrar mensaje de 茅xito
          setSuccessMessage(`Se guardaron los datos de la secci贸n ${step} correctamente`);
          setTimeout(() => setSuccessMessage(null), 3000);

          return updatedClient;
        } catch (err: any) {
          console.error(` Error actualizando datos parciales en paso ${step}:`, err);
          setError(`Error al guardar: ${err.message || 'Error desconocido'}`);
          throw err;
        }
      }
    } catch (error: any) {
      console.error(` Error en handleSavePartial paso ${step}:`, error);
      setError(error.message || `Error al guardar la secci贸n ${step}. Por favor, intente nuevamente.`);
      throw error;
    } finally {
      setSectionSubmitting(false);
    }
  };

  // Helper to ensure we have all required fields
  const ensureRequiredFields = (clientData: Partial<Client>) => {
    // Handle advisor_id assignment
    if (user && user.role === 'ADVISOR') {
      clientData.advisor_id = user.id;
    }

    // Handle the company_id requirement - only set default if not selected in form
    if (!clientData.company_id) {
      console.log(' No se seleccion贸 empresa en el formulario, aplicando l贸gica predeterminada');

      if (user) {
        if (user.role === 'COMPANY_ADMIN') {
          clientData.company_id = user.entityId;
          console.log(' Usando company_id del usuario COMPANY_ADMIN:', clientData.company_id);
        } else {
          // For other roles without company selection, use default
          clientData.company_id = DEFAULT_COMPANY_ID;
          console.log(' Usando company_id predeterminado para usuario con rol:', user.role);
        }
      } else {
        // No user information available, use default company_id
        clientData.company_id = DEFAULT_COMPANY_ID;
        console.log(' Sin contexto de usuario, usando company_id predeterminado');
      }
    } else {
      console.log(' Usando company_id seleccionado en el formulario:', clientData.company_id);
    }
  };

  // Helper to collect fields based on step
  const collectFieldsForStep = (data: Partial<Client>, step: number): Partial<Client> => {
    const result: Partial<Client> = {};

    // Always preserve these fields if they exist
    if (data.id) result.id = data.id;
    if (data.company_id) result.company_id = data.company_id;
    if (data.advisor_id) result.advisor_id = data.advisor_id;

    switch(step) {
      case 1: // Personal data
        const personalFields = [
          'first_name', 'paternal_surname', 'maternal_surname', 'email', 'phone',
          'rfc', 'curp', 'birth_date', 'gender', 'marital_status', 'address',
          'city', 'state', 'postal_code'
        ];
        personalFields.forEach(field => {
          if (field in data) {
            (result as any)[field] = (data as any)[field];
          }
        });
        break;

      case 2: // Financial details
        const financialFields = [
          'employment_type', 'employment_years', 'monthly_income',
          'additional_income', 'monthly_expenses', 'other_loan_balances'
        ];
        financialFields.forEach(field => {
          if (field in data) {
            (result as any)[field] = (data as any)[field];
          }
        });
        break;

      case 3: // Bank details
        const bankFields = [
          'bank_name', 'bank_clabe', 'bank_account_number',
          'bank_account_type', 'bank_account_origin'
        ];
        bankFields.forEach(field => {
          if (field in data) {
            (result as any)[field] = (data as any)[field];
          }
        });
        break;

      default:
        console.warn(` Paso desconocido para guardado parcial: ${step}`);
    }

    return result;
  };

  return (
    <MainLayout>
      <div className="p-6">
        <div className="flex items-center mb-6">
          <button 
            onClick={() => navigate('/clients')} 
            className="btn btn-sm btn-outline mr-4"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Volver
          </button>
          <h1 className="text-2xl font-bold">Nuevo Cliente</h1>
        </div>

        {/* Display error message */}
        {error && (
          <div className="mb-4">
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{error}</span>
              <button onClick={() => setError(null)} className="ml-auto"></button>
            </div>
          </div>
        )}

        {/* Display success message */}
        {successMessage && (
          <div className="mb-4">
            <div className="alert alert-success">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{successMessage}</span>
              <button onClick={() => setSuccessMessage(null)} className="ml-auto"></button>
            </div>
          </div>
        )}

        <ClientForm 
          initialData={draftClient || undefined}
          onSubmit={handleSubmit}
          onSavePartial={handleSavePartial}
          isSubmitting={isSubmitting}
        />
      </div>
    </MainLayout>
  );
};

export default NewClient;
