import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import MainLayout from '../components/layout/MainLayout';
import { supabase } from '../lib/supabaseClient';
import { STORAGE_BUCKET_NAME } from '../services/clientService';
import {
  Client,
  ClientDocument,
  getClientById,
  getClientApplications,
  getClientDocuments,
  uploadClientDocument,
  deleteClientDocument,
  getClientDataCompletionPercentage
} from '../services/clientService';
import { PERMISSIONS } from '../utils/constants/permissions';
import { usePermissions } from '../contexts/PermissionsContext';
import { toast } from 'react-hot-toast';
import { ChangeEvent, useRef } from 'react';

interface FileUploadProps {
  onFileUpload: (file: File, category: string) => void;
  clientId: string;
  isLoading: boolean;
}

const FileUpload: React.FC<FileUploadProps> = ({ onFileUpload, clientId, isLoading }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [category, setCategory] = useState<string>('');
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleCategoryChange = (event: ChangeEvent<HTMLInputElement>) => {
    setCategory(event.target.value);
  };

  const handleUpload = () => {
    if (selectedFile && clientId) {
      onFileUpload(selectedFile, category || 'Sin categoría');
      setSelectedFile(null);
      setCategory('');
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    } else {
      toast.error('Seleccione un archivo para subir.');
    }
  };

  return (
    <div className="mt-4 p-4 border border-dashed border-base-300 rounded-lg">
      <h3 className="font-semibold mb-2">Subir Nuevo Documento</h3>
      <div className="flex flex-col sm:flex-row gap-4 items-end">
        <div className="form-control w-full sm:w-auto flex-grow">
          <label className="label"><span className="label-text">Archivo</span></label>
          <input 
            ref={fileInputRef}
            type="file" 
            onChange={handleFileChange} 
            className="file-input file-input-bordered file-input-sm w-full" 
            disabled={isLoading}
          />
        </div>
        <div className="form-control w-full sm:w-auto">
          <label className="label"><span className="label-text">Categoría (Opcional)</span></label>
          <input 
            type="text" 
            placeholder="Ej: INE, Comprobante Domicilio"
            value={category}
            onChange={handleCategoryChange}
            className="input input-bordered input-sm w-full"
            disabled={isLoading}
          />
        </div>
        <button 
          onClick={handleUpload} 
          className="btn btn-sm btn-primary mt-2 sm:mt-0" 
          disabled={!selectedFile || isLoading}
        >
          {isLoading ? <span className="loading loading-spinner loading-xs"></span> : 'Subir'}
        </button>
      </div>
    </div>
  );
};

const ClientDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { userCan } = usePermissions();
  const [client, setClient] = useState<Client | null>(null);
  const [applications, setApplications] = useState<any[]>([]);
  const [documents, setDocuments] = useState<ClientDocument[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [uploading, setUploading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      fetchClientData(id);
    }
  }, [id]);

  const fetchClientData = async (clientId: string) => {
    try {
      setLoading(true);
      setError(null);

      const [clientData, applicationsData, documentsData] = await Promise.all([
        getClientById(clientId),
        getClientApplications(clientId),
        getClientDocuments(clientId)
      ]);

      setClient(clientData);
      setApplications(applicationsData || []);
      setDocuments(documentsData || []);

      if (!clientData) {
        setError('Cliente no encontrado.');
      }

    } catch (err: unknown) {
      console.error('Error fetching client data:', err);
      setError('No se pudieron cargar los datos del cliente. Por favor intente nuevamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (file: File, category: string) => {
    if (!id) return;
    setUploading(true);
    try {
      const newDocument = await uploadClientDocument(id, file, category);
      if (newDocument) {
        setDocuments(prev => [newDocument, ...prev]);
        toast.success('Documento subido correctamente.');
      } else {
         toast.error('No se pudo subir el documento.');
      }
    } catch (error) {
      console.error('Error uploading document:', error);
      toast.error('Error al subir el documento.');
    } finally {
      setUploading(false);
    }
  };

  const handleDeleteDocument = async (documentId: string) => {
    try {
      setDocuments(prev => prev.filter(doc => doc.id !== documentId));
      const success = await deleteClientDocument(documentId);
      if (success) {
        toast.success('Documento eliminado.');
      } else {
        toast.error('No se pudo eliminar el documento.');
        if (id) fetchClientData(id); 
      }
    } catch (error) {
      console.error('Error deleting document:', error);
      toast.error('Error al eliminar el documento.');
      if (id) fetchClientData(id); 
    }
  };

  const completionPercentage = client ? getClientDataCompletionPercentage(client) : 0;

  return (
    <MainLayout>
      <div className="p-6">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => navigate('/clients')} 
              className="btn btn-sm btn-outline"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
              </svg>
              Volver a Clientes
            </button>
            <h1 className="text-2xl font-bold">Detalle de Cliente</h1>
          </div>

          {client && userCan(PERMISSIONS.EDIT_CLIENT) && (
            <Link to={`/clients/${id}/edit`} className="btn btn-primary">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                <path d="M13.586 3.586a2 2 0 112.828 2.828l-.793.793-2.828-2.828.793-.793zM11.379 5.793L3 14.172V17h2.828l8.38-8.379-2.83-2.828z" />
              </svg>
              Editar Cliente
            </Link>
          )}
        </div>

        {loading ? (
          <div className="flex justify-center items-center py-12">
            <span className="loading loading-spinner loading-lg"></span>
          </div>
        ) : error ? (
          <div className="alert alert-error">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{error}</span>
          </div>
        ) : client ? (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-2 space-y-6">
              <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                  <div className="flex justify-between items-start mb-4">
                      <h2 className="card-title text-xl">Información General</h2>
                      <div className="text-right w-1/3">
                          <span className="text-sm font-medium">Completitud: {completionPercentage}%</span>
                          <progress
                            className={`progress ${
                              completionPercentage < 50 ? 'progress-error' :
                              completionPercentage < 90 ? 'progress-warning' :
                              'progress-success'
                            } w-full mt-1`}
                            value={completionPercentage}
                            max="100"
                          ></progress>
                      </div>
                  </div>

                  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-x-4 gap-y-3 text-sm">
                    <div><span className="text-gray-500 block">Nombre:</span><span className="font-medium">{client.name}</span></div>
                    <div><span className="text-gray-500 block">Email:</span><span className="font-medium">{client.email || '-'}</span></div>
                    <div><span className="text-gray-500 block">Teléfono:</span><span className="font-medium">{client.phone || '-'}</span></div>
                    <div><span className="text-gray-500 block">Fecha Nacimiento:</span><span className="font-medium">{client.birth_date ? new Date(client.birth_date + 'T00:00:00').toLocaleDateString('es-MX') : '-'}</span></div>
                    <div><span className="text-gray-500 block">Género:</span><span className="font-medium">{client.gender || '-'}</span></div>
                    <div><span className="text-gray-500 block">Estado Civil:</span><span className="font-medium">{client.marital_status || '-'}</span></div>
                    <div><span className="text-gray-500 block">RFC:</span><span className="font-medium">{client.rfc || '-'}</span></div>
                    <div><span className="text-gray-500 block">CURP:</span><span className="font-medium">{client.curp || '-'}</span></div>
                     <div><span className="text-gray-500 block">Fecha Registro:</span><span className="font-medium">{new Date(client.created_at).toLocaleDateString('es-MX')}</span></div>
                  </div>
                </div>
              </div>

              <div className="card bg-base-100 shadow-xl">
                 <div className="card-body">
                    <h2 className="card-title text-xl mb-4">Empleo e Ingresos</h2>
                     <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-x-4 gap-y-3 text-sm">
                        <div><span className="text-gray-500 block">Tipo Empleo:</span><span className="font-medium">{client.employment_type || '-'}</span></div>
                        <div><span className="text-gray-500 block">Antigüedad (Años):</span><span className="font-medium">{client.employment_years ?? '-'}</span></div>
                        <div><span className="text-gray-500 block">Ingreso Mensual:</span><span className="font-medium">{client.monthly_income?.toLocaleString('es-MX', { style: 'currency', currency: 'MXN' }) ?? '-'}</span></div>
                        <div><span className="text-gray-500 block">Ingresos Adicionales:</span><span className="font-medium">{client.additional_income?.toLocaleString('es-MX', { style: 'currency', currency: 'MXN' }) ?? '-'}</span></div>
                        <div><span className="text-gray-500 block">Gastos Mensuales:</span><span className="font-medium">{client.monthly_expenses?.toLocaleString('es-MX', { style: 'currency', currency: 'MXN' }) ?? '-'}</span></div>
                        <div><span className="text-gray-500 block">Saldo Otros Créditos:</span><span className="font-medium">{client.other_loan_balances?.toLocaleString('es-MX', { style: 'currency', currency: 'MXN' }) ?? '-'}</span></div>
                    </div>
                 </div>
              </div>

              <div className="card bg-base-100 shadow-xl">
                 <div className="card-body">
                    <h2 className="card-title text-xl mb-4">Datos Bancarios</h2>
                     <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-x-4 gap-y-3 text-sm">
                        <div><span className="text-gray-500 block">Banco:</span><span className="font-medium">{client.bank_name || '-'}</span></div>
                        <div><span className="text-gray-500 block">CLABE:</span><span className="font-medium">{client.bank_clabe || '-'}</span></div>
                        <div><span className="text-gray-500 block">No. Cuenta:</span><span className="font-medium">{client.bank_account_number || '-'}</span></div>
                        <div><span className="text-gray-500 block">Tipo Cuenta:</span><span className="font-medium">{client.bank_account_type || '-'}</span></div>
                        <div><span className="text-gray-500 block">Origen Cuenta:</span><span className="font-medium">{client.bank_account_origin || '-'}</span></div>
                    </div>
                 </div>
              </div>

            </div>

            <div className="lg:col-span-1 space-y-6">
              <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                  <h2 className="card-title text-xl mb-4">Dirección</h2>
                  <div className="space-y-2 text-sm">
                    <div><span className="text-gray-500 block">Dirección:</span><span className="font-medium">{client.address || '-'}</span></div>
                    <div><span className="text-gray-500 block">Ciudad:</span><span className="font-medium">{client.city || '-'}</span></div>
                    <div><span className="text-gray-500 block">Estado:</span><span className="font-medium">{client.state || '-'}</span></div>
                    <div><span className="text-gray-500 block">Código Postal:</span><span className="font-medium">{client.postal_code || '-'}</span></div>
                  </div>
                </div>
              </div>

              <div className="card bg-base-100 shadow-xl">
                 <div className="card-body">
                    <h2 className="card-title text-xl mb-4">Documentos</h2>

                    {documents.length === 0 ? (
                       <p className="text-sm text-gray-500 italic">No hay documentos asociados a este cliente.</p>
                    ) : (
                      <ul className="menu p-0">
                        {documents.map(doc => (
                          <li key={doc.id} className="border-b border-base-200 last:border-b-0">
                            <div className="flex justify-between items-center w-full">
                              <div className="flex-grow overflow-hidden mr-2">
                                <a 
                                  href={supabase.storage.from(STORAGE_BUCKET_NAME).getPublicUrl(doc.file_path).data.publicUrl} 
                                  target="_blank" 
                                  rel="noopener noreferrer" 
                                  className="link link-hover text-sm truncate block"
                                  title={doc.file_name}
                                >
                                  {doc.file_name}
                                </a>
                                <span className="text-xs text-gray-500 block">
                                  {doc.category || 'Sin categoría'} - {new Date(doc.created_at).toLocaleDateString('es-MX')}
                                  {doc.file_size ? ` (${(doc.file_size / 1024 / 1024).toFixed(2)} MB)` : ''}
                                </span>
                              </div>
                              <button 
                                onClick={() => handleDeleteDocument(doc.id)} 
                                className="btn btn-xs btn-error btn-outline btn-square"
                                title="Eliminar documento"
                                disabled={uploading}
                              >
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                                  <path strokeLinecap="round" strokeLinejoin="round" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                                </svg>
                              </button>
                            </div>
                          </li>
                        ))}
                      </ul>
                    )}

                    <FileUpload 
                        onFileUpload={handleFileUpload} 
                        clientId={client.id} 
                        isLoading={uploading} 
                    />
                 </div>
              </div>

            </div>

            <div className="card bg-base-100 shadow-xl lg:col-span-3">
              <div className="card-body">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="card-title text-xl">Solicitudes</h2>
                  {userCan(PERMISSIONS.CREATE_APPLICATION) && (
                    <Link to={`/applications/new?client=${id}`} className="btn btn-sm btn-primary">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
                      </svg>
                      Nueva Solicitud
                    </Link>
                  )}
                </div>

                {!applications || applications.length === 0 ? (
                  <div className="flex flex-col items-center justify-center py-10 text-center">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 text-gray-300 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                    </svg>
                    <p className="text-gray-500 mb-4">Este cliente no tiene solicitudes registradas.</p>
                    {userCan(PERMISSIONS.CREATE_APPLICATION) && (
                      <Link to={`/applications/new?client=${id}`} className="btn btn-primary">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
                        </svg>
                        Crear Solicitud
                      </Link>
                    )}
                  </div>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="table w-full">
                      <thead>
                        <tr>
                          <th>ID</th>
                          <th>Fecha</th>
                          <th>Tipo</th>
                          <th>Monto</th>
                          <th>Estado</th>
                          <th>Acciones</th>
                        </tr>
                      </thead>
                      <tbody>
                        {applications.map(app => (
                          <tr key={app.id} className="hover">
                            <td>{app.id.substring(0, 8)}...</td>
                            <td>
                              {new Date(app.created_at).toLocaleDateString('es-MX', {
                                day: '2-digit',
                                month: '2-digit',
                                year: 'numeric'
                              })}
                            </td>
                            <td>{app.application_type}</td>
                            <td>${app.amount.toLocaleString('es-MX')}</td>
                            <td>
                              <span className={`badge ${
                                app.status === 'approved' ? 'badge-success' :
                                app.status === 'rejected' ? 'badge-error' :
                                app.status === 'in_review' ? 'badge-warning' :
                                'badge-info'
                              }`}>
                                {app.status}
                              </span>
                            </td>
                            <td>
                              <Link to={`/applications/${app.id}`} className="btn btn-xs btn-info">
                                Ver
                              </Link>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            </div>
          </div>
        ) : (
          <div className="alert alert-warning">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
            </svg>
            <span>Cliente no encontrado.</span>
          </div>
        )}
      </div>
    </MainLayout>
  );
};

export default ClientDetail;
