          <div 
            className="bg-primary h-2.5 rounded-full" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      </div>

      {/* Step Indicator */}
      <div className="mb-6">
        <ul className="steps steps-horizontal w-full">
          <li className={`step ${currentStep >= 1 ? 'step-primary' : ''}`}>Datos Personales</li>
          <li className={`step ${currentStep >= 2 ? 'step-primary' : ''}`}>Datos Financieros</li>
          <li className={`step ${currentStep >= 3 ? 'step-primary' : ''}`}>Datos Bancarios</li>
          <li className={`step ${currentStep >= 4 ? 'step-primary' : ''}`}>Documentos</li>
        </ul>
      </div>

      {/* Error Display */}
      {Object.keys(errors).length > 0 && (
        <div className="alert alert-error mb-4" id="error-message">
          <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <div>
            <h3 className="font-bold">Por favor corrige los siguientes errores:</h3>
            <ul className="list-disc ml-5 mt-1">
              {Object.entries(errors).map(([field, message]) => (
                <li key={field}>{message}</li>
              ))}
            </ul>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {renderStep()}

        {/* Navigation Buttons */}
        <div className="flex flex-wrap justify-between mt-6">
          {currentStep > 1 && (
            <button 
              type="button"
              className="btn btn-outline btn-primary"
              onClick={prevStep}
              disabled={isSubmitting}
            >
              Anterior
            </button>
          )}

          <div className="flex space-x-2">
            {/* Cancel Button (Step 1 only) */}
            {currentStep === 1 && (
              <button 
                type="button"
                className="btn btn-ghost"
                onClick={() => navigate('/clients')}
                disabled={isSubmitting}
              >
                Cancelar
              </button>
            )}

            {/* "Save and Exit" button for intermediate steps */}
            {currentStep > 1 && currentStep < 4 && onSavePartial && (
              <button 
                type="button"
                className="btn btn-outline btn-secondary"
                onClick={handleSaveAndExit}
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Guardando...' : 'Guardar y Salir'}
              </button>
            )}

            {/* Next step button (steps 1-3) */}
            {currentStep < 4 && (
              <button 
                type="button" 
                className="btn btn-primary"
                onClick={currentStep < 3 ? handleSaveAndContinue : nextStep}
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Guardando...' : currentStep === 3 ? 'Siguiente' : 'Guardar y Continuar'}
              </button>
            )}

            {/* Submit button (step 4) */}
            {currentStep === 4 && (
              <button 
                type="submit" 
                className="btn btn-success"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Guardando...' : 'Guardar Cliente'}
              </button>
            )}
          </div>
        </div>
      </form>
    </div>
  );
};

