import { supabase } from '../lib/supabaseClient';
import { TABLES } from '../utils/constants/tables';

export interface Document {
  id: string;
  created_at: string;
  file_name: string;
  file_path: string;
  file_type: string;
  file_size: number;
  category?: string;
  application_id?: string;
  client_id?: string;
  uploaded_by_user_id?: string;
  is_verified?: boolean;
  verified_by?: string;
  verified_at?: string;
}

export interface DocumentUpload {
  file: File;
  application_id?: string;
  client_id?: string;
  userId: string;
  documentName: string;
  description?: string;
  category?: string;
}

const DOCUMENTS_TABLE = TABLES.DOCUMENTS;
const STORAGE_BUCKET = 'documents';

// Get all documents for an application
export const getApplicationDocuments = async (applicationId: string) => {
  const { data, error } = await supabase
    .from(DOCUMENTS_TABLE)
    .select('*')
    .eq('application_id', applicationId)
    .order('created_at', { ascending: false });

  if (error) {
    console.error(`Error fetching documents for application ${applicationId}:`, error);
    throw error;
  }

  return data;
};

// Get all documents for a client
export const getClientDocuments = async (clientId: string) => {
  const { data, error } = await supabase
    .from(DOCUMENTS_TABLE)
    .select('*')
    .eq('client_id', clientId)
    .order('created_at', { ascending: false });

  if (error) {
    console.error(`Error fetching documents for client ${clientId}:`, error);
    throw error;
  }

  return data;
};

// Get a single document by ID
export const getDocumentById = async (id: string) => {
  const { data, error } = await supabase
    .from(DOCUMENTS_TABLE)
    .select('*')
    .eq('id', id)
    .single();

  if (error) {
    console.error(`Error fetching document with ID ${id}:`, error);
    throw error;
  }

  return data as Document;
};

// Upload a document
export const uploadDocument = async (upload: DocumentUpload) => {
  const { file, application_id, client_id, userId, documentName, description, category } = upload;

  try {
    if (!file) {
      console.error('Upload failed: No file provided');
      throw new Error("El archivo es requerido para la carga");
    }

    if (!application_id && !client_id) {
      console.error('Upload failed: No application_id or client_id provided');
      throw new Error("Se debe proporcionar un ID de solicitud o ID de cliente");
    }

    // Check file size (max 10MB)
    if (file.size > 10 * 1024 * 1024) {
      console.error('Upload failed: File too large', file.size);
      throw new Error("El archivo es demasiado grande (máximo 10MB)");
    }

    // Generate a unique file path
    const timestamp = new Date().getTime();
    const fileName = file.name.replace(/[^a-zA-Z0-9.]/g, '_'); // Sanitize filename
    const fileExtension = fileName.split('.').pop() || 'unknown';
    const prefix = application_id ? `applications/${application_id}` : `clients/${client_id}`;
    const filePath = `${prefix}/${timestamp}_${fileName}`;

    console.log(`Uploading document: ${documentName} to path: ${filePath}`);

    // Attempt file upload with retry logic (max 2 attempts)
    let storageError = null;
    let uploadResult = null;

    for (let attempt = 0; attempt < 2; attempt++) {
      try {
        const { data, error } = await supabase.storage
          .from(STORAGE_BUCKET)
          .upload(filePath, file, {
            cacheControl: '3600',
            upsert: attempt > 0 // On retry, allow overwrite
          });

        if (error) {
          console.error(`Upload attempt ${attempt + 1} failed:`, error);
          storageError = error;
        } else {
          uploadResult = data;
          storageError = null;
          break; // Success, exit retry loop
        }
      } catch (err) {
        console.error(`Unexpected error on upload attempt ${attempt + 1}:`, err);
        storageError = err;
      }

      // Wait before retry
      if (attempt < 1) {
        await new Promise(r => setTimeout(r, 1000));
      }
    }

    if (storageError) {
      console.error('All upload attempts failed:', storageError);
      // Use a type-safe approach to access error message
      const errorMessage = storageError instanceof Error 
        ? storageError.message 
        : (typeof storageError === 'object' && storageError !== null && 'message' in storageError)
          ? String(storageError.message)
          : 'Error desconocido';

      throw new Error(`Error al subir el archivo: ${errorMessage}`);
    }

    // Create document record in database
    const documentRecord = {
      file_name: documentName || fileName,
      file_path: filePath,
      file_type: file.type || `application/${fileExtension}`,
      file_size: file.size,
      uploaded_by_user_id: userId,
      category
    };

    if (application_id) {
      Object.assign(documentRecord, { application_id });
    }

    if (client_id) {
      Object.assign(documentRecord, { client_id });
    }

    if (description) {
      Object.assign(documentRecord, { description });
    }

    console.log('Creating document record in database');
    const { data: documentData, error: documentError } = await supabase
      .from(DOCUMENTS_TABLE)
      .insert([documentRecord])
      .select();

    if (documentError) {
      console.error('Error creating document record:', documentError);

      // Clean up: delete the uploaded file if document record creation failed
      try {
        await supabase.storage
          .from(STORAGE_BUCKET)
          .remove([filePath]);
        console.log('Cleaned up file after database record creation failure');
      } catch (cleanupError) {
        console.error('Error during cleanup of uploaded file:', cleanupError);
      }

      // Use a type-safe approach to access error message  
      const errorMessage = documentError instanceof Error 
        ? documentError.message 
        : (typeof documentError === 'object' && documentError !== null && 'message' in documentError)
          ? String((documentError as {message: string}).message)
          : 'Error desconocido';

      throw new Error(`Error al guardar el registro del documento: ${errorMessage}`);
    }

    console.log('Document uploaded successfully:', documentData[0]?.id);
    return documentData[0] as Document;
  } catch (error) {
    console.error('Critical error in document upload process:', error);
    throw error;
  }
};

// Delete a document
export const deleteDocument = async (documentId: string) => {
  // Get document to get file path
  const { data: document, error: fetchError } = await supabase
    .from(DOCUMENTS_TABLE)
    .select('file_path')
    .eq('id', documentId)
    .single();

  if (fetchError) {
    console.error(`Error fetching document ${documentId} for deletion:`, fetchError);
    throw fetchError;
  }

  // Delete file from storage
  const { error: storageError } = await supabase.storage
    .from(STORAGE_BUCKET)
    .remove([document.file_path]);

  if (storageError) {
    console.error(`Error deleting file for document ${documentId}:`, storageError);
    throw storageError;
  }

  // Delete document record
  const { error: deleteError } = await supabase
    .from(DOCUMENTS_TABLE)
    .delete()
    .eq('id', documentId);

  if (deleteError) {
    console.error(`Error deleting document record ${documentId}:`, deleteError);
    throw deleteError;
  }

  return true;
};

// Get document download URL
export const getDocumentUrl = async (filePath: string) => {
  const { data, error } = await supabase.storage
    .from(STORAGE_BUCKET)
    .createSignedUrl(filePath, 60 * 60); // 1 hour expiry

  if (error) {
    console.error(`Error getting URL for document ${filePath}:`, error);
    throw error;
  }

  return data.signedUrl;
};

// Verify a document
export const verifyDocument = async (documentId: string, userId: string, isVerified: boolean = true) => {
  const { data, error } = await supabase
    .from(DOCUMENTS_TABLE)
    .update({
      is_verified: isVerified,
      verified_by: userId,
      verified_at: new Date().toISOString()
    })
    .eq('id', documentId)
    .select();

  if (error) {
    console.error(`Error verifying document ${documentId}:`, error);
    throw error;
  }

  return data[0] as Document;
};

// Get required documents (based on application type)
export const getRequiredDocuments = async (applicationType: string) => {
  const { data, error } = await supabase
    .from('required_documents')
    .select('*')
    .eq('application_type', applicationType);

  if (error) {
    console.error(`Error fetching required documents for ${applicationType}:`, error);
    throw error;
  }

  return data;
};
