import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Application } from '../../services/applicationService';
import { STATUS_COLORS, APPLICATION_STATUS, STATUS_LABELS } from '../../utils/constants/statuses';

interface KanbanBoardProps {
  applications: Application[];
  onStatusChange?: (application: Application, newStatus: string) => Promise<void>;
}

const KanbanBoard: React.FC<KanbanBoardProps> = ({ applications, onStatusChange }) => {
  const [draggingId, setDraggingId] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  // Agrupar aplicaciones por estatus
  const groupedApplications = applications.reduce((acc, app) => {
    if (!acc[app.status]) {
      acc[app.status] = [];
    }
    acc[app.status].push(app);
    return acc;
  }, {} as Record<string, Application[]>);

  // Asegurar que todas las columnas de estatus estén presentes
  const statuses = Object.values(APPLICATION_STATUS);
  const columns = statuses.map(status => ({
    id: status,
    title: STATUS_LABELS[status as keyof typeof STATUS_LABELS],
    color: STATUS_COLORS[status as keyof typeof STATUS_COLORS],
    applications: groupedApplications[status] || []
  }));

  const handleDragStart = (applicationId: string) => {
    setDraggingId(applicationId);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = async (e: React.DragEvent, newStatus: string) => {
    e.preventDefault();

    if (!draggingId || !onStatusChange) return;

    const application = applications.find(app => app.id === draggingId);
    if (!application) return;

    // No hacer nada si el estatus es el mismo
    if (application.status === newStatus) {
      setDraggingId(null);
      return;
    }

    try {
      setIsLoading(true);
      setErrorMessage(null);
      await onStatusChange(application, newStatus);
    } catch (error: any) {
      setErrorMessage(`Error al actualizar el estatus: ${error.message}`);
      console.error("Error updating status:", error);
    } finally {
      setIsLoading(false);
      setDraggingId(null);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(amount);
  };

  return (
    <div className="w-full overflow-x-auto">
      {isLoading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl flex flex-col items-center">
            <span className="loading loading-spinner loading-lg"></span>
            <p className="mt-4 text-lg font-medium">Actualizando estado...</p>
          </div>
        </div>
      )}

      {errorMessage && (
        <div className="mb-4 alert alert-error shadow-lg">
          <div>
            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current flex-shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{errorMessage}</span>
          </div>
          <div className="flex-none">
            <button className="btn btn-sm" onClick={() => setErrorMessage(null)}>Cerrar</button>
          </div>
        </div>
      )}

      <div className="flex space-x-4 pb-4 kanban-container" style={{ minWidth: '1000px' }}>
        {columns.map(column => (
          <div 
            key={column.id}
            className={`flex-1 bg-base-200 rounded-box p-4 kanban-column`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, column.id)}
          >
            <div className={`text-center mb-4 font-bold bg-${column.color} text-${column.color}-content p-2 rounded-md`}>
              {column.title} <span className="badge badge-sm">{column.applications.length}</span>
            </div>

            <div className="space-y-3 min-h-[400px]">
              {column.applications.map(app => (
                <div
                  key={app.id}
                  className="card bg-base-100 shadow-sm hover:shadow-md cursor-move transition-shadow"
                  draggable={!!onStatusChange}
                  onDragStart={() => handleDragStart(app.id)}
                >
                  <div className="card-body p-4">
                    <div className="flex justify-between items-center">
                      <h3 className="text-sm font-semibold truncate">{app.client_name}</h3>
                      <span className="badge badge-primary badge-sm">{app.application_type}</span>
                    </div>

                    <div className="mt-2">
                      <div className="text-xs text-gray-500">
                        {app.client_email}<br/>
                        {app.client_phone}
                      </div>
                      <div className="mt-2 text-sm font-medium">
                        {formatCurrency(app.amount)}
                      </div>
                    </div>

                    <div className="card-actions justify-end mt-2">
                      <Link to={`/applications/${app.id}`} className="btn btn-xs btn-outline">
                        Ver
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanBoard;
